fi

# Remove $HOME/.local/share/claude
echo "Removing $HOME/.local/share/claude..."
if [ -d "$HOME/.local/share/claude" ]; then
    rm -rf "$HOME/.local/share/claude" 2>/dev/null || true
elif [ -f "$HOME/.local/share/claude" ]; then
    rm -f "$HOME/.local/share/claude" 2>/dev/null || true
fi

# Remove $HOME/.local/state/claude
echo "Removing $HOME/.local/state/claude..."
