#!/bin/bash
# Uninstall script for Cleartext
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Cleartext..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Cleartext if running..."
pkill -f "Cleartext" 2>/dev/null || true

# Remove /Applications/Cleartext.app
echo "Removing /Applications/Cleartext.app..."
if [ -d "/Applications/Cleartext.app" ]; then
    rm -rf "/Applications/Cleartext.app" 2>/dev/null || true
elif [ -f "/Applications/Cleartext.app" ]; then
    rm -f "/Applications/Cleartext.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
