#!/bin/bash
# Uninstall script for ClipGrab
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling ClipGrab..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping ClipGrab if running..."
pkill -f "ClipGrab" 2>/dev/null || true

# Remove /Applications/ClipGrab.app
echo "Removing /Applications/ClipGrab.app..."
if [ -d "/Applications/ClipGrab.app" ]; then
    rm -rf "/Applications/ClipGrab.app" 2>/dev/null || true
elif [ -f "/Applications/ClipGrab.app" ]; then
    rm -f "/Applications/ClipGrab.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/de.clipgrab.ClipGrab.plist
echo "Removing $HOME/Library/Preferences/de.clipgrab.ClipGrab.plist..."
if [ -d "$HOME/Library/Preferences/de.clipgrab.ClipGrab.plist" ]; then
    rm -rf "$HOME/Library/Preferences/de.clipgrab.ClipGrab.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/de.clipgrab.ClipGrab.plist" ]; then
    rm -f "$HOME/Library/Preferences/de.clipgrab.ClipGrab.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
