#!/bin/bash
# Uninstall script for CloudCompare
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling CloudCompare..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping CloudCompare if running..."
pkill -f "CloudCompare" 2>/dev/null || true

# Remove /Applications/CloudCompare.app
echo "Removing /Applications/CloudCompare.app..."
if [ -d "/Applications/CloudCompare.app" ]; then
    rm -rf "/Applications/CloudCompare.app" 2>/dev/null || true
elif [ -f "/Applications/CloudCompare.app" ]; then
    rm -f "/Applications/CloudCompare.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.cccorp.CloudCompare.plist
echo "Removing $HOME/Library/Preferences/com.cccorp.CloudCompare.plist..."
if [ -d "$HOME/Library/Preferences/com.cccorp.CloudCompare.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.cccorp.CloudCompare.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.cccorp.CloudCompare.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.cccorp.CloudCompare.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
