#!/bin/bash
# Uninstall script for ColorWell
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling ColorWell..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping ColorWell if running..."
pkill -f "ColorWell" 2>/dev/null || true

# Remove /Applications/ColorWell.app
echo "Removing /Applications/ColorWell.app..."
if [ -d "/Applications/ColorWell.app" ]; then
    rm -rf "/Applications/ColorWell.app" 2>/dev/null || true
elif [ -f "/Applications/ColorWell.app" ]; then
    rm -f "/Applications/ColorWell.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.sweetpproductions.ColorWell
echo "Removing $HOME/Library/Application Scripts/com.sweetpproductions.ColorWell..."
if [ -d "$HOME/Library/Application Scripts/com.sweetpproductions.ColorWell" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.sweetpproductions.ColorWell" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.sweetpproductions.ColorWell" ]; then
    rm -f "$HOME/Library/Application Scripts/com.sweetpproductions.ColorWell" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.sweetpproductions.ColorWell
echo "Removing $HOME/Library/Containers/com.sweetpproductions.ColorWell..."
if [ -d "$HOME/Library/Containers/com.sweetpproductions.ColorWell" ]; then
