#!/bin/bash
# Uninstall script for Colour Contrast Analyser
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Colour Contrast Analyser..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Colour Contrast Analyser if running..."
pkill -f "Colour Contrast Analyser" 2>/dev/null || true

# Remove /Applications/Colour Contrast Analyser.app
echo "Removing /Applications/Colour Contrast Analyser.app..."
if [ -d "/Applications/Colour Contrast Analyser.app" ]; then
    rm -rf "/Applications/Colour Contrast Analyser.app" 2>/dev/null || true
elif [ -f "/Applications/Colour Contrast Analyser.app" ]; then
    rm -f "/Applications/Colour Contrast Analyser.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.electron.cca.plist
echo "Removing $HOME/Library/Preferences/com.electron.cca.plist..."
if [ -d "$HOME/Library/Preferences/com.electron.cca.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.electron.cca.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.electron.cca.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.electron.cca.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.electron.cca.savedState
echo "Removing $HOME/Library/Saved Application State/com.electron.cca.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.electron.cca.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.electron.cca.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.electron.cca.savedState" ]; then
