#!/bin/bash
# Uninstall script for Compositor
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Compositor..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Compositor if running..."
pkill -f "Compositor" 2>/dev/null || true

# Remove /Applications/Compositor.app
echo "Removing /Applications/Compositor.app..."
if [ -d "/Applications/Compositor.app" ]; then
    rm -rf "/Applications/Compositor.app" 2>/dev/null || true
elif [ -f "/Applications/Compositor.app" ]; then
    rm -f "/Applications/Compositor.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.microlarge.Compositor
echo "Removing $HOME/Library/Application Scripts/com.microlarge.Compositor..."
if [ -d "$HOME/Library/Application Scripts/com.microlarge.Compositor" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.microlarge.Compositor" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.microlarge.Compositor" ]; then
    rm -f "$HOME/Library/Application Scripts/com.microlarge.Compositor" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.microlarge.Compositor
echo "Removing $HOME/Library/Containers/com.microlarge.Compositor..."
if [ -d "$HOME/Library/Containers/com.microlarge.Compositor" ]; then
    rm -rf "$HOME/Library/Containers/com.microlarge.Compositor" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.microlarge.Compositor" ]; then
    rm -f "$HOME/Library/Containers/com.microlarge.Compositor" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.microlarge.Compositor.plist
echo "Removing $HOME/Library/Preferences/com.microlarge.Compositor.plist..."
if [ -d "$HOME/Library/Preferences/com.microlarge.Compositor.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.microlarge.Compositor.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.microlarge.Compositor.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.microlarge.Compositor.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
