#!/bin/bash
# Uninstall script for ConnectMeNow
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling ConnectMeNow..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping ConnectMeNow if running..."
pkill -f "ConnectMeNow" 2>/dev/null || true

# Remove /Applications/ConnectMeNow4.app
echo "Removing /Applications/ConnectMeNow4.app..."
if [ -d "/Applications/ConnectMeNow4.app" ]; then
    rm -rf "/Applications/ConnectMeNow4.app" 2>/dev/null || true
elif [ -f "/Applications/ConnectMeNow4.app" ]; then
    rm -f "/Applications/ConnectMeNow4.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.Tweaking4All.ConnectMeNow4.*
echo "Removing $HOME/Library/Preferences/com.Tweaking4All.ConnectMeNow4.*..."
if [ -d "$HOME/Library/Preferences/com.Tweaking4All.ConnectMeNow4.*" ]; then
    rm -rf "$HOME/Library/Preferences/com.Tweaking4All.ConnectMeNow4.*" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.Tweaking4All.ConnectMeNow4.*" ]; then
    rm -f "$HOME/Library/Preferences/com.Tweaking4All.ConnectMeNow4.*" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
