#!/bin/bash
# Uninstall script for CoolTerm
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling CoolTerm..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping CoolTerm if running..."
pkill -f "CoolTerm" 2>/dev/null || true

# Remove /Applications/CoolTerm.app
echo "Removing /Applications/CoolTerm.app..."
if [ -d "/Applications/CoolTerm.app" ]; then
    rm -rf "/Applications/CoolTerm.app" 2>/dev/null || true
elif [ -f "/Applications/CoolTerm.app" ]; then
    rm -f "/Applications/CoolTerm.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/CoolTerm_Prefs.plist
echo "Removing $HOME/Library/Preferences/CoolTerm_Prefs.plist..."
if [ -d "$HOME/Library/Preferences/CoolTerm_Prefs.plist" ]; then
    rm -rf "$HOME/Library/Preferences/CoolTerm_Prefs.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/CoolTerm_Prefs.plist" ]; then
    rm -f "$HOME/Library/Preferences/CoolTerm_Prefs.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.the-meiers.coolterm.savedState
echo "Removing $HOME/Library/Saved Application State/org.the-meiers.coolterm.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.the-meiers.coolterm.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.the-meiers.coolterm.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.the-meiers.coolterm.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.the-meiers.coolterm.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
