#!/bin/bash
# Uninstall script for Craft
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Craft..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Craft if running..."
pkill -f "Craft" 2>/dev/null || true

# Remove /Applications/Craft.app
echo "Removing /Applications/Craft.app..."
if [ -d "/Applications/Craft.app" ]; then
    rm -rf "/Applications/Craft.app" 2>/dev/null || true
elif [ -f "/Applications/Craft.app" ]; then
    rm -f "/Applications/Craft.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.lukilabs.lukiapp.Craft*
echo "Removing $HOME/Library/Application Scripts/com.lukilabs.lukiapp.Craft*..."
if [ -d "$HOME/Library/Application Scripts/com.lukilabs.lukiapp.Craft*" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.lukilabs.lukiapp.Craft*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.lukilabs.lukiapp.Craft*" ]; then
    rm -f "$HOME/Library/Application Scripts/com.lukilabs.lukiapp.Craft*" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.lukilabs.lukiapp.Craft*
echo "Removing $HOME/Library/Containers/com.lukilabs.lukiapp.Craft*..."
if [ -d "$HOME/Library/Containers/com.lukilabs.lukiapp.Craft*" ]; then
    rm -rf "$HOME/Library/Containers/com.lukilabs.lukiapp.Craft*" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.lukilabs.lukiapp.Craft*" ]; then
    rm -f "$HOME/Library/Containers/com.lukilabs.lukiapp.Craft*" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
