#!/bin/bash
# Uninstall script for Crypter
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Crypter..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Crypter if running..."
pkill -f "Crypter" 2>/dev/null || true

# Remove /Applications/Crypter.app
echo "Removing /Applications/Crypter.app..."
if [ -d "/Applications/Crypter.app" ]; then
    rm -rf "/Applications/Crypter.app" 2>/dev/null || true
elif [ -f "/Applications/Crypter.app" ]; then
    rm -f "/Applications/Crypter.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Crypter
echo "Removing $HOME/Library/Application Support/Crypter..."
if [ -d "$HOME/Library/Application Support/Crypter" ]; then
    rm -rf "$HOME/Library/Application Support/Crypter" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Crypter" ]; then
    rm -f "$HOME/Library/Application Support/Crypter" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Crypter
echo "Removing $HOME/Library/Logs/Crypter..."
if [ -d "$HOME/Library/Logs/Crypter" ]; then
    rm -rf "$HOME/Library/Logs/Crypter" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Crypter" ]; then
