#!/bin/bash
# Uninstall script for CursorSense
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling CursorSense..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping CursorSense if running..."
pkill -f "CursorSense" 2>/dev/null || true

# Remove /Applications/CursorSense.app
echo "Removing /Applications/CursorSense.app..."
if [ -d "/Applications/CursorSense.app" ]; then
    rm -rf "/Applications/CursorSense.app" 2>/dev/null || true
elif [ -f "/Applications/CursorSense.app" ]; then
    rm -f "/Applications/CursorSense.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/SteerMouse & CursorSense
echo "Removing $HOME/Library/Application Support/SteerMouse & CursorSense..."
if [ -d "$HOME/Library/Application Support/SteerMouse & CursorSense" ]; then
    rm -rf "$HOME/Library/Application Support/SteerMouse & CursorSense" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/SteerMouse & CursorSense" ]; then
    rm -f "$HOME/Library/Application Support/SteerMouse & CursorSense" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/jp.plentycom.CursorSense.app
echo "Removing $HOME/Library/HTTPStorages/jp.plentycom.CursorSense.app..."
if [ -d "$HOME/Library/HTTPStorages/jp.plentycom.CursorSense.app" ]; then
    rm -rf "$HOME/Library/HTTPStorages/jp.plentycom.CursorSense.app" 2>/dev/null || true
elif [ -f "$HOME/Library/HTTPStorages/jp.plentycom.CursorSense.app" ]; then
    rm -f "$HOME/Library/HTTPStorages/jp.plentycom.CursorSense.app" 2>/dev/null || true
fi

# Remove $HOME/Library/LaunchAgents/jp.plentycom.CursorSense.boa.plist
echo "Removing $HOME/Library/LaunchAgents/jp.plentycom.CursorSense.boa.plist..."
if [ -d "$HOME/Library/LaunchAgents/jp.plentycom.CursorSense.boa.plist" ]; then
    rm -rf "$HOME/Library/LaunchAgents/jp.plentycom.CursorSense.boa.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/LaunchAgents/jp.plentycom.CursorSense.boa.plist" ]; then
    rm -f "$HOME/Library/LaunchAgents/jp.plentycom.CursorSense.boa.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/jp.plentycom.CursorSense.app.plist
echo "Removing $HOME/Library/Preferences/jp.plentycom.CursorSense.app.plist..."
if [ -d "$HOME/Library/Preferences/jp.plentycom.CursorSense.app.plist" ]; then
    rm -rf "$HOME/Library/Preferences/jp.plentycom.CursorSense.app.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/jp.plentycom.CursorSense.app.plist" ]; then
    rm -f "$HOME/Library/Preferences/jp.plentycom.CursorSense.app.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
