#!/bin/bash
# Uninstall script for Cursr
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Cursr..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Cursr if running..."
pkill -f "Cursr" 2>/dev/null || true

# Remove /Applications/Cursr.app
echo "Removing /Applications/Cursr.app..."
if [ -d "/Applications/Cursr.app" ]; then
    rm -rf "/Applications/Cursr.app" 2>/dev/null || true
elif [ -f "/Applications/Cursr.app" ]; then
    rm -f "/Applications/Cursr.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/cursr
echo "Removing $HOME/Library/Application Support/cursr..."
if [ -d "$HOME/Library/Application Support/cursr" ]; then
    rm -rf "$HOME/Library/Application Support/cursr" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/cursr" ]; then
    rm -f "$HOME/Library/Application Support/cursr" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.bitgapp.cursr.plist
echo "Removing $HOME/Library/Preferences/com.bitgapp.cursr.plist..."
if [ -d "$HOME/Library/Preferences/com.bitgapp.cursr.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.bitgapp.cursr.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.bitgapp.cursr.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.bitgapp.cursr.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
