#!/bin/bash
# Uninstall script for CustomShortcuts
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling CustomShortcuts..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping CustomShortcuts if running..."
pkill -f "CustomShortcuts" 2>/dev/null || true

# Remove /Applications/CustomShortcuts.app
echo "Removing /Applications/CustomShortcuts.app..."
if [ -d "/Applications/CustomShortcuts.app" ]; then
    rm -rf "/Applications/CustomShortcuts.app" 2>/dev/null || true
elif [ -f "/Applications/CustomShortcuts.app" ]; then
    rm -f "/Applications/CustomShortcuts.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.houdah.CustomShortcuts
echo "Removing $HOME/Library/Application Support/com.houdah.CustomShortcuts..."
if [ -d "$HOME/Library/Application Support/com.houdah.CustomShortcuts" ]; then
    rm -rf "$HOME/Library/Application Support/com.houdah.CustomShortcuts" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.houdah.CustomShortcuts" ]; then
    rm -f "$HOME/Library/Application Support/com.houdah.CustomShortcuts" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.houdah.CustomShortcuts
echo "Removing $HOME/Library/Caches/com.houdah.CustomShortcuts..."
if [ -d "$HOME/Library/Caches/com.houdah.CustomShortcuts" ]; then
    rm -rf "$HOME/Library/Caches/com.houdah.CustomShortcuts" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.houdah.CustomShortcuts" ]; then
    rm -f "$HOME/Library/Caches/com.houdah.CustomShortcuts" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.houdah.CustomShortcuts.plist
echo "Removing $HOME/Library/Preferences/com.houdah.CustomShortcuts.plist..."
if [ -d "$HOME/Library/Preferences/com.houdah.CustomShortcuts.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.houdah.CustomShortcuts.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.houdah.CustomShortcuts.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.houdah.CustomShortcuts.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
