#!/bin/bash
# Uninstall script for DarkModeBuddy
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DarkModeBuddy..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DarkModeBuddy if running..."
pkill -f "DarkModeBuddy" 2>/dev/null || true

# Remove /Applications/DarkModeBuddy.app
echo "Removing /Applications/DarkModeBuddy.app..."
if [ -d "/Applications/DarkModeBuddy.app" ]; then
    rm -rf "/Applications/DarkModeBuddy.app" 2>/dev/null || true
elif [ -f "/Applications/DarkModeBuddy.app" ]; then
    rm -f "/Applications/DarkModeBuddy.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/codes.rambo.DarkModeBuddy.plist
echo "Removing $HOME/Library/Preferences/codes.rambo.DarkModeBuddy.plist..."
if [ -d "$HOME/Library/Preferences/codes.rambo.DarkModeBuddy.plist" ]; then
    rm -rf "$HOME/Library/Preferences/codes.rambo.DarkModeBuddy.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/codes.rambo.DarkModeBuddy.plist" ]; then
    rm -f "$HOME/Library/Preferences/codes.rambo.DarkModeBuddy.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
