#!/bin/bash
# Uninstall script for darktable
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling darktable..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping darktable if running..."
pkill -f "darktable" 2>/dev/null || true

# Remove /Applications/darktable.app
echo "Removing /Applications/darktable.app..."
if [ -d "/Applications/darktable.app" ]; then
    rm -rf "/Applications/darktable.app" 2>/dev/null || true
elif [ -f "/Applications/darktable.app" ]; then
    rm -f "/Applications/darktable.app" 2>/dev/null || true
fi

# Remove $HOME/.cache/darktable
echo "Removing $HOME/.cache/darktable..."
if [ -d "$HOME/.cache/darktable" ]; then
    rm -rf "$HOME/.cache/darktable" 2>/dev/null || true
elif [ -f "$HOME/.cache/darktable" ]; then
    rm -f "$HOME/.cache/darktable" 2>/dev/null || true
fi

# Remove $HOME/.config/darktable
echo "Removing $HOME/.config/darktable..."
if [ -d "$HOME/.config/darktable" ]; then
    rm -rf "$HOME/.config/darktable" 2>/dev/null || true
elif [ -f "$HOME/.config/darktable" ]; then
    rm -f "$HOME/.config/darktable" 2>/dev/null || true
fi

# Remove $HOME/.local/share/darktable
echo "Removing $HOME/.local/share/darktable..."
if [ -d "$HOME/.local/share/darktable" ]; then
    rm -rf "$HOME/.local/share/darktable" 2>/dev/null || true
elif [ -f "$HOME/.local/share/darktable" ]; then
    rm -f "$HOME/.local/share/darktable" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.darktable.savedState
echo "Removing $HOME/Library/Saved Application State/org.darktable.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.darktable.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.darktable.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.darktable.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.darktable.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
