#!/bin/bash
# Uninstall script for DeepGit
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DeepGit..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DeepGit if running..."
pkill -f "DeepGit" 2>/dev/null || true

# Remove /Applications/DeepGit.app
echo "Removing /Applications/DeepGit.app..."
if [ -d "/Applications/DeepGit.app" ]; then
    rm -rf "/Applications/DeepGit.app" 2>/dev/null || true
elif [ -f "/Applications/DeepGit.app" ]; then
    rm -f "/Applications/DeepGit.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.syntevo.deepgit.plist
echo "Removing $HOME/Library/Preferences/com.syntevo.deepgit.plist..."
if [ -d "$HOME/Library/Preferences/com.syntevo.deepgit.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.syntevo.deepgit.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.syntevo.deepgit.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.syntevo.deepgit.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/DeepGit
echo "Removing $HOME/Library/Preferences/DeepGit..."
if [ -d "$HOME/Library/Preferences/DeepGit" ]; then
    rm -rf "$HOME/Library/Preferences/DeepGit" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/DeepGit" ]; then
    rm -f "$HOME/Library/Preferences/DeepGit" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.syntevo.deepgit.savedState
echo "Removing $HOME/Library/Saved Application State/com.syntevo.deepgit.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.syntevo.deepgit.savedState" ]; then
