elif [ -f "$HOME/Library/Saved Application State/com.syntevo.deepgit.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.syntevo.deepgit.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
