#!/bin/bash
# Uninstall script for Defold
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Defold..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Defold if running..."
pkill -f "Defold" 2>/dev/null || true

# Remove /Applications/Defold.app
echo "Removing /Applications/Defold.app..."
if [ -d "/Applications/Defold.app" ]; then
    rm -rf "/Applications/Defold.app" 2>/dev/null || true
elif [ -f "/Applications/Defold.app" ]; then
    rm -f "/Applications/Defold.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Defold
echo "Removing $HOME/Library/Application Support/Defold..."
if [ -d "$HOME/Library/Application Support/Defold" ]; then
    rm -rf "$HOME/Library/Application Support/Defold" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Defold" ]; then
    rm -f "$HOME/Library/Application Support/Defold" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.defold.editor
echo "Removing $HOME/Library/Caches/com.defold.editor..."
if [ -d "$HOME/Library/Caches/com.defold.editor" ]; then
    rm -rf "$HOME/Library/Caches/com.defold.editor" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.defold.editor" ]; then
    rm -f "$HOME/Library/Caches/com.defold.editor" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.defold.editor.plist
echo "Removing $HOME/Library/Preferences/com.defold.editor.plist..."
if [ -d "$HOME/Library/Preferences/com.defold.editor.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.defold.editor.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.defold.editor.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.defold.editor.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.defold.editor.savedState
echo "Removing $HOME/Library/Saved Application State/com.defold.editor.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.defold.editor.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.defold.editor.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.defold.editor.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.defold.editor.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
