#!/bin/bash
# Uninstall script for DevKnife
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DevKnife..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DevKnife if running..."
pkill -f "DevKnife" 2>/dev/null || true

# Remove /Applications/DevKnife.app
echo "Removing /Applications/DevKnife.app..."
if [ -d "/Applications/DevKnife.app" ]; then
    rm -rf "/Applications/DevKnife.app" 2>/dev/null || true
elif [ -f "/Applications/DevKnife.app" ]; then
    rm -f "/Applications/DevKnife.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.solotuna.devknife/
echo "Removing $HOME/Library/Application Support/com.solotuna.devknife/..."
if [ -d "$HOME/Library/Application Support/com.solotuna.devknife/" ]; then
    rm -rf "$HOME/Library/Application Support/com.solotuna.devknife/" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.solotuna.devknife/" ]; then
    rm -f "$HOME/Library/Application Support/com.solotuna.devknife/" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.solotuna.devknife.plist
echo "Removing $HOME/Library/Preferences/com.solotuna.devknife.plist..."
if [ -d "$HOME/Library/Preferences/com.solotuna.devknife.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.solotuna.devknife.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.solotuna.devknife.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.solotuna.devknife.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
