#!/bin/bash
# Uninstall script for Diffusion Bee
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Diffusion Bee..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Diffusion Bee if running..."
pkill -f "Diffusion Bee" 2>/dev/null || true

# Remove /Applications/DiffusionBee.app
echo "Removing /Applications/DiffusionBee.app..."
if [ -d "/Applications/DiffusionBee.app" ]; then
    rm -rf "/Applications/DiffusionBee.app" 2>/dev/null || true
elif [ -f "/Applications/DiffusionBee.app" ]; then
    rm -f "/Applications/DiffusionBee.app" 2>/dev/null || true
fi

# Remove $HOME/.diffusionbee
echo "Removing $HOME/.diffusionbee..."
if [ -d "$HOME/.diffusionbee" ]; then
    rm -rf "$HOME/.diffusionbee" 2>/dev/null || true
elif [ -f "$HOME/.diffusionbee" ]; then
    rm -f "$HOME/.diffusionbee" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/DiffusionBee
echo "Removing $HOME/Library/Application Support/DiffusionBee..."
if [ -d "$HOME/Library/Application Support/DiffusionBee" ]; then
    rm -rf "$HOME/Library/Application Support/DiffusionBee" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/DiffusionBee" ]; then
    rm -f "$HOME/Library/Application Support/DiffusionBee" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
