#!/bin/bash
# Uninstall script for digiKam
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling digiKam..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping digiKam if running..."
pkill -f "digiKam" 2>/dev/null || true

# Remove $HOME/Library/Application Support/digikam
echo "Removing $HOME/Library/Application Support/digikam..."
if [ -d "$HOME/Library/Application Support/digikam" ]; then
    rm -rf "$HOME/Library/Application Support/digikam" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/digikam" ]; then
    rm -f "$HOME/Library/Application Support/digikam" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/digikam
echo "Removing $HOME/Library/Caches/digikam..."
if [ -d "$HOME/Library/Caches/digikam" ]; then
    rm -rf "$HOME/Library/Caches/digikam" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/digikam" ]; then
    rm -f "$HOME/Library/Caches/digikam" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/digikam.plist
echo "Removing $HOME/Library/Preferences/digikam.plist..."
if [ -d "$HOME/Library/Preferences/digikam.plist" ]; then
    rm -rf "$HOME/Library/Preferences/digikam.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/digikam.plist" ]; then
    rm -f "$HOME/Library/Preferences/digikam.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/digikamrc
echo "Removing $HOME/Library/Preferences/digikamrc..."
if [ -d "$HOME/Library/Preferences/digikamrc" ]; then
    rm -rf "$HOME/Library/Preferences/digikamrc" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/digikamrc" ]; then
    rm -f "$HOME/Library/Preferences/digikamrc" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/digikam.savedState
echo "Removing $HOME/Library/Saved Application State/digikam.savedState..."
if [ -d "$HOME/Library/Saved Application State/digikam.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/digikam.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/digikam.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/digikam.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
