#!/bin/bash
# Uninstall script for Disk Inventory X
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Disk Inventory X..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Disk Inventory X if running..."
pkill -f "Disk Inventory X" 2>/dev/null || true

# Remove /Applications/Disk Inventory X.app
echo "Removing /Applications/Disk Inventory X.app..."
if [ -d "/Applications/Disk Inventory X.app" ]; then
    rm -rf "/Applications/Disk Inventory X.app" 2>/dev/null || true
elif [ -f "/Applications/Disk Inventory X.app" ]; then
    rm -f "/Applications/Disk Inventory X.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.derlien.DiskInventoryX.plist
echo "Removing $HOME/Library/Preferences/com.derlien.DiskInventoryX.plist..."
if [ -d "$HOME/Library/Preferences/com.derlien.DiskInventoryX.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.derlien.DiskInventoryX.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.derlien.DiskInventoryX.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.derlien.DiskInventoryX.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
