#!/bin/bash
# Uninstall script for DisplayCAL
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DisplayCAL..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DisplayCAL if running..."
pkill -f "DisplayCAL" 2>/dev/null || true

# Remove $HOME/Library/Application Support/dispcalGUI
echo "Removing $HOME/Library/Application Support/dispcalGUI..."
if [ -d "$HOME/Library/Application Support/dispcalGUI" ]; then
    rm -rf "$HOME/Library/Application Support/dispcalGUI" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/dispcalGUI" ]; then
    rm -f "$HOME/Library/Application Support/dispcalGUI" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/DisplayCAL
echo "Removing $HOME/Library/Application Support/DisplayCAL..."
if [ -d "$HOME/Library/Application Support/DisplayCAL" ]; then
    rm -rf "$HOME/Library/Application Support/DisplayCAL" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/DisplayCAL" ]; then
    rm -f "$HOME/Library/Application Support/DisplayCAL" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/dispcalGUI
echo "Removing $HOME/Library/Logs/dispcalGUI..."
if [ -d "$HOME/Library/Logs/dispcalGUI" ]; then
    rm -rf "$HOME/Library/Logs/dispcalGUI" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/dispcalGUI" ]; then
    rm -f "$HOME/Library/Logs/dispcalGUI" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/DisplayCAL
echo "Removing $HOME/Library/Logs/DisplayCAL..."
if [ -d "$HOME/Library/Logs/DisplayCAL" ]; then
    rm -rf "$HOME/Library/Logs/DisplayCAL" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/DisplayCAL" ]; then
    rm -f "$HOME/Library/Logs/DisplayCAL" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/dispcalGUI
echo "Removing $HOME/Library/Preferences/dispcalGUI..."
if [ -d "$HOME/Library/Preferences/dispcalGUI" ]; then
    rm -rf "$HOME/Library/Preferences/dispcalGUI" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/dispcalGUI" ]; then
    rm -f "$HOME/Library/Preferences/dispcalGUI" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/DisplayCAL
echo "Removing $HOME/Library/Preferences/DisplayCAL..."
if [ -d "$HOME/Library/Preferences/DisplayCAL" ]; then
    rm -rf "$HOME/Library/Preferences/DisplayCAL" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/DisplayCAL" ]; then
    rm -f "$HOME/Library/Preferences/DisplayCAL" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
