#!/bin/bash
# Uninstall script for DjView
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DjView..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DjView if running..."
pkill -f "DjView" 2>/dev/null || true

# Remove /Applications/DjView.app
echo "Removing /Applications/DjView.app..."
if [ -d "/Applications/DjView.app" ]; then
    rm -rf "/Applications/DjView.app" 2>/dev/null || true
elif [ -f "/Applications/DjView.app" ]; then
    rm -f "/Applications/DjView.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.djvu.djview.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.djvu.djview.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.djvu.djview.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.djvu.djview.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.djvu.djview.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.djvu.djview.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.djvu.DjView.plist
echo "Removing $HOME/Library/Preferences/org.djvu.DjView.plist..."
if [ -d "$HOME/Library/Preferences/org.djvu.DjView.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.djvu.DjView.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.djvu.DjView.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.djvu.DjView.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.djvu.DjView.savedState
echo "Removing $HOME/Library/Saved Application State/org.djvu.DjView.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.djvu.DjView.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.djvu.DjView.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.djvu.DjView.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.djvu.DjView.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
