#!/bin/bash
# Uninstall script for DockStation
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DockStation..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DockStation if running..."
pkill -f "DockStation" 2>/dev/null || true

# Remove /Applications/DockStation.app
echo "Removing /Applications/DockStation.app..."
if [ -d "/Applications/DockStation.app" ]; then
    rm -rf "/Applications/DockStation.app" 2>/dev/null || true
elif [ -f "/Applications/DockStation.app" ]; then
    rm -f "/Applications/DockStation.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/dockstation
echo "Removing $HOME/Library/Application Support/dockstation..."
if [ -d "$HOME/Library/Application Support/dockstation" ]; then
    rm -rf "$HOME/Library/Application Support/dockstation" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/dockstation" ]; then
    rm -f "$HOME/Library/Application Support/dockstation" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.dockstation.DockStation.helper.plist
echo "Removing $HOME/Library/Preferences/org.dockstation.DockStation.helper.plist..."
if [ -d "$HOME/Library/Preferences/org.dockstation.DockStation.helper.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.dockstation.DockStation.helper.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.dockstation.DockStation.helper.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.dockstation.DockStation.helper.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.dockstation.DockStation.plist
echo "Removing $HOME/Library/Preferences/org.dockstation.DockStation.plist..."
if [ -d "$HOME/Library/Preferences/org.dockstation.DockStation.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.dockstation.DockStation.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.dockstation.DockStation.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.dockstation.DockStation.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.dockstation.DockStation.savedState
echo "Removing $HOME/Library/Saved Application State/org.dockstation.DockStation.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.dockstation.DockStation.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.dockstation.DockStation.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.dockstation.DockStation.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.dockstation.DockStation.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
