#!/bin/bash
# Uninstall script for Double Commander
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Double Commander..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Double Commander if running..."
pkill -f "Double Commander" 2>/dev/null || true

# Remove /Applications/Double Commander.app
echo "Removing /Applications/Double Commander.app..."
if [ -d "/Applications/Double Commander.app" ]; then
    rm -rf "/Applications/Double Commander.app" 2>/dev/null || true
elif [ -f "/Applications/Double Commander.app" ]; then
    rm -f "/Applications/Double Commander.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/doublecmd
echo "Removing $HOME/Library/Caches/doublecmd..."
if [ -d "$HOME/Library/Caches/doublecmd" ]; then
    rm -rf "$HOME/Library/Caches/doublecmd" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/doublecmd" ]; then
    rm -f "$HOME/Library/Caches/doublecmd" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
