#!/bin/bash
# Uninstall script for Droplr
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Droplr..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Droplr if running..."
pkill -f "Droplr" 2>/dev/null || true

# Remove $HOME/Library/Application Scripts/com.droplr.droplr-mac
echo "Removing $HOME/Library/Application Scripts/com.droplr.droplr-mac..."
if [ -d "$HOME/Library/Application Scripts/com.droplr.droplr-mac" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.droplr.droplr-mac" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.droplr.droplr-mac" ]; then
    rm -f "$HOME/Library/Application Scripts/com.droplr.droplr-mac" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.droplr.droplr-mac
echo "Removing $HOME/Library/Containers/com.droplr.droplr-mac..."
if [ -d "$HOME/Library/Containers/com.droplr.droplr-mac" ]; then
    rm -rf "$HOME/Library/Containers/com.droplr.droplr-mac" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.droplr.droplr-mac" ]; then
    rm -f "$HOME/Library/Containers/com.droplr.droplr-mac" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
