#!/bin/bash
# Uninstall script for Dust3D
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Dust3D..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Dust3D if running..."
pkill -f "Dust3D" 2>/dev/null || true

# Remove /Applications/dust3d-1.0.0-rc.9.app
echo "Removing /Applications/dust3d-1.0.0-rc.9.app..."
if [ -d "/Applications/dust3d-1.0.0-rc.9.app" ]; then
    rm -rf "/Applications/dust3d-1.0.0-rc.9.app" 2>/dev/null || true
elif [ -f "/Applications/dust3d-1.0.0-rc.9.app" ]; then
    rm -f "/Applications/dust3d-1.0.0-rc.9.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.yourcompany.dust3d.savedState
echo "Removing $HOME/Library/Saved Application State/com.yourcompany.dust3d.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.yourcompany.dust3d.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.yourcompany.dust3d.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.yourcompany.dust3d.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.yourcompany.dust3d.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
