#!/bin/bash
# Uninstall script for Dynalist
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Dynalist..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Dynalist if running..."
pkill -f "Dynalist" 2>/dev/null || true

# Remove /Applications/Dynalist.app
echo "Removing /Applications/Dynalist.app..."
if [ -d "/Applications/Dynalist.app" ]; then
    rm -rf "/Applications/Dynalist.app" 2>/dev/null || true
elif [ -f "/Applications/Dynalist.app" ]; then
    rm -f "/Applications/Dynalist.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
