#!/bin/bash
# Uninstall script for Eclipse Installer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Eclipse Installer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Eclipse Installer if running..."
pkill -f "Eclipse Installer" 2>/dev/null || true

# Remove /Applications/Eclipse Installer.app
echo "Removing /Applications/Eclipse Installer.app..."
if [ -d "/Applications/Eclipse Installer.app" ]; then
    rm -rf "/Applications/Eclipse Installer.app" 2>/dev/null || true
elif [ -f "/Applications/Eclipse Installer.app" ]; then
    rm -f "/Applications/Eclipse Installer.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.eclipse.oomph.setup.installer.product.with-jre.restricted.plist
echo "Removing $HOME/Library/Preferences/org.eclipse.oomph.setup.installer.product.with-jre.restricted.plist..."
if [ -d "$HOME/Library/Preferences/org.eclipse.oomph.setup.installer.product.with-jre.restricted.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.eclipse.oomph.setup.installer.product.with-jre.restricted.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.eclipse.oomph.setup.installer.product.with-jre.restricted.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.eclipse.oomph.setup.installer.product.with-jre.restricted.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.eclipse.oomph.setup.installer.product.with-jre.restricted.savedState
echo "Removing $HOME/Library/Saved Application State/org.eclipse.oomph.setup.installer.product.with-jre.restricted.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.eclipse.oomph.setup.installer.product.with-jre.restricted.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.eclipse.oomph.setup.installer.product.with-jre.restricted.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.eclipse.oomph.setup.installer.product.with-jre.restricted.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.eclipse.oomph.setup.installer.product.with-jre.restricted.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
