#!/bin/bash
# Uninstall script for Electron Cash
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Electron Cash..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Electron Cash if running..."
pkill -f "Electron Cash" 2>/dev/null || true

# Remove /Applications/Electron-Cash.app
echo "Removing /Applications/Electron-Cash.app..."
if [ -d "/Applications/Electron-Cash.app" ]; then
    rm -rf "/Applications/Electron-Cash.app" 2>/dev/null || true
elif [ -f "/Applications/Electron-Cash.app" ]; then
    rm -f "/Applications/Electron-Cash.app" 2>/dev/null || true
fi

# Remove $HOME/.electron-cash
echo "Removing $HOME/.electron-cash..."
if [ -d "$HOME/.electron-cash" ]; then
    rm -rf "$HOME/.electron-cash" 2>/dev/null || true
elif [ -f "$HOME/.electron-cash" ]; then
    rm -f "$HOME/.electron-cash" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
