#!/bin/bash
# Uninstall script for Electrum
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Electrum..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Electrum if running..."
pkill -f "Electrum" 2>/dev/null || true

# Remove /Applications/Electrum.app
echo "Removing /Applications/Electrum.app..."
if [ -d "/Applications/Electrum.app" ]; then
    rm -rf "/Applications/Electrum.app" 2>/dev/null || true
elif [ -f "/Applications/Electrum.app" ]; then
    rm -f "/Applications/Electrum.app" 2>/dev/null || true
fi

# Remove $HOME/.electrum
echo "Removing $HOME/.electrum..."
if [ -d "$HOME/.electrum" ]; then
    rm -rf "$HOME/.electrum" 2>/dev/null || true
elif [ -f "$HOME/.electrum" ]; then
    rm -f "$HOME/.electrum" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Electrum.plist
echo "Removing $HOME/Library/Preferences/Electrum.plist..."
if [ -d "$HOME/Library/Preferences/Electrum.plist" ]; then
    rm -rf "$HOME/Library/Preferences/Electrum.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/Electrum.plist" ]; then
    rm -f "$HOME/Library/Preferences/Electrum.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.org.pythonmac.unspecified.Electrum.plist
echo "Removing $HOME/Library/Preferences/org.org.pythonmac.unspecified.Electrum.plist..."
if [ -d "$HOME/Library/Preferences/org.org.pythonmac.unspecified.Electrum.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.org.pythonmac.unspecified.Electrum.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.org.pythonmac.unspecified.Electrum.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.org.pythonmac.unspecified.Electrum.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/Electrum.savedState
echo "Removing $HOME/Library/Saved Application State/Electrum.savedState..."
if [ -d "$HOME/Library/Saved Application State/Electrum.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/Electrum.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/Electrum.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/Electrum.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
