#!/bin/bash
# Uninstall script for Elgato Camera Hub
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Elgato Camera Hub..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Elgato Camera Hub if running..."
pkill -f "Elgato Camera Hub" 2>/dev/null || true

# Unload service com.displaylink.loginscreen
echo "Unloading service com.displaylink.loginscreen..."
launchctl unload -w /Library/LaunchAgents/com.displaylink.loginscreen.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.displaylink.loginscreen.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.displaylink.loginscreen.plist 2>/dev/null || true

# Unload service com.displaylink.XpcService
echo "Unloading service com.displaylink.XpcService..."
launchctl unload -w /Library/LaunchAgents/com.displaylink.XpcService.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.displaylink.XpcService.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.displaylink.XpcService.plist 2>/dev/null || true

# Unload service com.elgato.CameraHub
echo "Unloading service com.elgato.CameraHub..."
launchctl unload -w /Library/LaunchAgents/com.elgato.CameraHub.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.elgato.CameraHub.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.elgato.CameraHub.plist 2>/dev/null || true

# Kill application with bundle ID com.displaylink.DisplayLinkUserAgent if running
echo "Stopping application with bundle ID com.displaylink.DisplayLinkUserAgent if running..."
killall -9 "com.displaylink.DisplayLinkUserAgent" 2>/dev/null || true

# Remove $HOME/Library/Logs/CameraHub
echo "Removing $HOME/Library/Logs/CameraHub..."
if [ -d "$HOME/Library/Logs/CameraHub" ]; then
    rm -rf "$HOME/Library/Logs/CameraHub" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/CameraHub" ]; then
    rm -f "$HOME/Library/Logs/CameraHub" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.elgato.CameraHub.plist
echo "Removing $HOME/Library/Preferences/com.elgato.CameraHub.plist..."
if [ -d "$HOME/Library/Preferences/com.elgato.CameraHub.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.elgato.CameraHub.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.elgato.CameraHub.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.elgato.CameraHub.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
