#!/bin/bash
# Uninstall script for eM Client
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling eM Client..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping eM Client if running..."
pkill -f "eM Client" 2>/dev/null || true

# Remove $HOME/Library/Caches/com.emclient.mail.client
echo "Removing $HOME/Library/Caches/com.emclient.mail.client..."
if [ -d "$HOME/Library/Caches/com.emclient.mail.client" ]; then
    rm -rf "$HOME/Library/Caches/com.emclient.mail.client" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.emclient.mail.client" ]; then
    rm -f "$HOME/Library/Caches/com.emclient.mail.client" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.emclient.mail.*.plist
echo "Removing $HOME/Library/Preferences/com.emclient.mail.*.plist..."
if [ -d "$HOME/Library/Preferences/com.emclient.mail.*.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.emclient.mail.*.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.emclient.mail.*.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.emclient.mail.*.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.emclient.mail.client.savedState
echo "Removing $HOME/Library/Saved Application State/com.emclient.mail.client.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.emclient.mail.client.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.emclient.mail.client.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.emclient.mail.client.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.emclient.mail.client.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
