#!/bin/bash
# Uninstall script for Enclave
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Enclave..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Enclave if running..."
pkill -f "Enclave" 2>/dev/null || true

# Unload service com.enclave
echo "Unloading service com.enclave..."
launchctl unload -w /Library/LaunchAgents/com.enclave.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.enclave.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.enclave.plist 2>/dev/null || true

# Remove /usr/local/bin//Applications/enclave/enclave
echo "Removing /usr/local/bin//Applications/enclave/enclave..."
if [ -d "/usr/local/bin//Applications/enclave/enclave" ]; then
    rm -rf "/usr/local/bin//Applications/enclave/enclave" 2>/dev/null || true
elif [ -f "/usr/local/bin//Applications/enclave/enclave" ]; then
    rm -f "/usr/local/bin//Applications/enclave/enclave" 2>/dev/null || true
fi

# Remove /etc/enclave
echo "Removing /etc/enclave..."
if [ -d "/etc/enclave" ]; then
    rm -rf "/etc/enclave" 2>/dev/null || true
elif [ -f "/etc/enclave" ]; then
    rm -f "/etc/enclave" 2>/dev/null || true
fi

# Remove /var/log/enclave
echo "Removing /var/log/enclave..."
if [ -d "/var/log/enclave" ]; then
    rm -rf "/var/log/enclave" 2>/dev/null || true
elif [ -f "/var/log/enclave" ]; then
    rm -f "/var/log/enclave" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
