#!/bin/bash
# Uninstall script for Equinox
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Equinox..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Equinox if running..."
pkill -f "Equinox" 2>/dev/null || true

# Remove /Applications/Equinox.app
echo "Removing /Applications/Equinox.app..."
if [ -d "/Applications/Equinox.app" ]; then
    rm -rf "/Applications/Equinox.app" 2>/dev/null || true
elif [ -f "/Applications/Equinox.app" ]; then
    rm -f "/Applications/Equinox.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.rlxone.equinox
echo "Removing $HOME/Library/Application Scripts/com.rlxone.equinox..."
if [ -d "$HOME/Library/Application Scripts/com.rlxone.equinox" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.rlxone.equinox" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.rlxone.equinox" ]; then
    rm -f "$HOME/Library/Application Scripts/com.rlxone.equinox" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.rlxone.equinox
echo "Removing $HOME/Library/Containers/com.rlxone.equinox..."
if [ -d "$HOME/Library/Containers/com.rlxone.equinox" ]; then
    rm -rf "$HOME/Library/Containers/com.rlxone.equinox" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.rlxone.equinox" ]; then
    rm -f "$HOME/Library/Containers/com.rlxone.equinox" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
