#!/bin/bash
# Uninstall script for EVKey
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling EVKey..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping EVKey if running..."
