#!/bin/bash
# Uninstall script for ExifCleaner
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling ExifCleaner..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping ExifCleaner if running..."
pkill -f "ExifCleaner" 2>/dev/null || true

# Remove /Applications/ExifCleaner.app
echo "Removing /Applications/ExifCleaner.app..."
if [ -d "/Applications/ExifCleaner.app" ]; then
    rm -rf "/Applications/ExifCleaner.app" 2>/dev/null || true
elif [ -f "/Applications/ExifCleaner.app" ]; then
    rm -f "/Applications/ExifCleaner.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/ExifCleaner
echo "Removing $HOME/Library/Application Support/ExifCleaner..."
if [ -d "$HOME/Library/Application Support/ExifCleaner" ]; then
    rm -rf "$HOME/Library/Application Support/ExifCleaner" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/ExifCleaner" ]; then
    rm -f "$HOME/Library/Application Support/ExifCleaner" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.exifcleaner.savedState
echo "Removing $HOME/Library/Saved Application State/com.exifcleaner.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.exifcleaner.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.exifcleaner.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.exifcleaner.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.exifcleaner.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
