#!/bin/bash
# Uninstall script for ExifRenamer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling ExifRenamer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping ExifRenamer if running..."
pkill -f "ExifRenamer" 2>/dev/null || true

# Remove /Applications/ExifRenamer.app
echo "Removing /Applications/ExifRenamer.app..."
if [ -d "/Applications/ExifRenamer.app" ]; then
    rm -rf "/Applications/ExifRenamer.app" 2>/dev/null || true
elif [ -f "/Applications/ExifRenamer.app" ]; then
    rm -f "/Applications/ExifRenamer.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/de.qdev.ExifRenamer.plist
echo "Removing $HOME/Library/Preferences/de.qdev.ExifRenamer.plist..."
if [ -d "$HOME/Library/Preferences/de.qdev.ExifRenamer.plist" ]; then
    rm -rf "$HOME/Library/Preferences/de.qdev.ExifRenamer.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/de.qdev.ExifRenamer.plist" ]; then
    rm -f "$HOME/Library/Preferences/de.qdev.ExifRenamer.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
