#!/bin/bash
# Uninstall script for far2l
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling far2l..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping far2l if running..."
pkill -f "far2l" 2>/dev/null || true

# Remove /Applications/far2l.app
echo "Removing /Applications/far2l.app..."
if [ -d "/Applications/far2l.app" ]; then
    rm -rf "/Applications/far2l.app" 2>/dev/null || true
elif [ -f "/Applications/far2l.app" ]; then
    rm -f "/Applications/far2l.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.far2l.savedState
echo "Removing $HOME/Library/Saved Application State/com.far2l.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.far2l.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.far2l.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.far2l.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.far2l.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
