#!/bin/bash
# Uninstall script for Fastmail
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Fastmail..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Fastmail if running..."
pkill -f "Fastmail" 2>/dev/null || true

# Remove /Applications/Fastmail.app
echo "Removing /Applications/Fastmail.app..."
if [ -d "/Applications/Fastmail.app" ]; then
    rm -rf "/Applications/Fastmail.app" 2>/dev/null || true
elif [ -f "/Applications/Fastmail.app" ]; then
    rm -f "/Applications/Fastmail.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.fastmail.mac.fastmail.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.fastmail.mac.fastmail.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.fastmail.mac.fastmail.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.fastmail.mac.fastmail.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.fastmail.mac.fastmail.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.fastmail.mac.fastmail.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Fastmail
echo "Removing $HOME/Library/Application Support/Fastmail..."
if [ -d "$HOME/Library/Application Support/Fastmail" ]; then
    rm -rf "$HOME/Library/Application Support/Fastmail" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Fastmail" ]; then
    rm -f "$HOME/Library/Application Support/Fastmail" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.fastmail.mac.Fastmail.plist
echo "Removing $HOME/Library/Preferences/com.fastmail.mac.Fastmail.plist..."
if [ -d "$HOME/Library/Preferences/com.fastmail.mac.Fastmail.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.fastmail.mac.Fastmail.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.fastmail.mac.Fastmail.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.fastmail.mac.Fastmail.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
