# Remove /Applications/FastScripts.app
echo "Removing /Applications/FastScripts.app..."
if [ -d "/Applications/FastScripts.app" ]; then
    rm -rf "/Applications/FastScripts.app" 2>/dev/null || true
elif [ -f "/Applications/FastScripts.app" ]; then
    rm -f "/Applications/FastScripts.app" 2>/dev/null || true
fi

