#!/bin/bash
# Uninstall script for Fellow
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Fellow..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Fellow if running..."
pkill -f "Fellow" 2>/dev/null || true

# Remove /Applications/Fellow.app
echo "Removing /Applications/Fellow.app..."
if [ -d "/Applications/Fellow.app" ]; then
    rm -rf "/Applications/Fellow.app" 2>/dev/null || true
elif [ -f "/Applications/Fellow.app" ]; then
    rm -f "/Applications/Fellow.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Fellow
echo "Removing $HOME/Library/Application Support/Fellow..."
if [ -d "$HOME/Library/Application Support/Fellow" ]; then
    rm -rf "$HOME/Library/Application Support/Fellow" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Fellow" ]; then
    rm -f "$HOME/Library/Application Support/Fellow" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.electron.fellow.plist
echo "Removing $HOME/Library/Preferences/com.electron.fellow.plist..."
if [ -d "$HOME/Library/Preferences/com.electron.fellow.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.electron.fellow.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.electron.fellow.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.electron.fellow.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
