#!/bin/bash
# Uninstall script for Fleet
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Fleet..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Fleet if running..."
pkill -f "Fleet" 2>/dev/null || true

# Remove /Applications/Fleet.app
echo "Removing /Applications/Fleet.app..."
if [ -d "/Applications/Fleet.app" ]; then
    rm -rf "/Applications/Fleet.app" 2>/dev/null || true
elif [ -f "/Applications/Fleet.app" ]; then
    rm -f "/Applications/Fleet.app" 2>/dev/null || true
fi

# Remove binary /Applications/Fleet.app/Fleet.app/Contents/app/bin/fleet
echo "Removing binary /Applications/Fleet.app/Fleet.app/Contents/app/bin/fleet..."
if [ -f "/Applications/Fleet.app/Fleet.app/Contents/app/bin/fleet" ]; then
    rm -f "/Applications/Fleet.app/Fleet.app/Contents/app/bin/fleet" 2>/dev/null || true
fi

# Remove $HOME/.fleet
echo "Removing $HOME/.fleet..."
if [ -d "$HOME/.fleet" ]; then
    rm -rf "$HOME/.fleet" 2>/dev/null || true
elif [ -f "$HOME/.fleet" ]; then
    rm -f "$HOME/.fleet" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/JetBrains/Fleet
echo "Removing $HOME/Library/Application Support/JetBrains/Fleet..."
if [ -d "$HOME/Library/Application Support/JetBrains/Fleet" ]; then
    rm -rf "$HOME/Library/Application Support/JetBrains/Fleet" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/JetBrains/Fleet" ]; then
    rm -f "$HOME/Library/Application Support/JetBrains/Fleet" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/JetBrains/Fleet
echo "Removing $HOME/Library/Caches/JetBrains/Fleet..."
if [ -d "$HOME/Library/Caches/JetBrains/Fleet" ]; then
    rm -rf "$HOME/Library/Caches/JetBrains/Fleet" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/JetBrains/Fleet" ]; then
    rm -f "$HOME/Library/Caches/JetBrains/Fleet" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/JetBrains/Fleet
echo "Removing $HOME/Library/Logs/JetBrains/Fleet..."
if [ -d "$HOME/Library/Logs/JetBrains/Fleet" ]; then
