#!/bin/bash
# Uninstall script for Fluid
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Fluid..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Fluid if running..."
pkill -f "Fluid" 2>/dev/null || true

# Remove /Applications/Fluid.app
echo "Removing /Applications/Fluid.app..."
if [ -d "/Applications/Fluid.app" ]; then
    rm -rf "/Applications/Fluid.app" 2>/dev/null || true
elif [ -f "/Applications/Fluid.app" ]; then
    rm -f "/Applications/Fluid.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Fluid
echo "Removing $HOME/Library/Application Support/Fluid..."
if [ -d "$HOME/Library/Application Support/Fluid" ]; then
    rm -rf "$HOME/Library/Application Support/Fluid" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Fluid" ]; then
    rm -f "$HOME/Library/Application Support/Fluid" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.fluidapp.Fluid2
echo "Removing $HOME/Library/Caches/com.fluidapp.Fluid2..."
if [ -d "$HOME/Library/Caches/com.fluidapp.Fluid2" ]; then
    rm -rf "$HOME/Library/Caches/com.fluidapp.Fluid2" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.fluidapp.Fluid2" ]; then
