fi

# Remove $HOME/Library/Saved Application State/com.fluidapp.Fluid*
echo "Removing $HOME/Library/Saved Application State/com.fluidapp.Fluid*..."
if [ -d "$HOME/Library/Saved Application State/com.fluidapp.Fluid*" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.fluidapp.Fluid*" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.fluidapp.Fluid*" ]; then
    rm -f "$HOME/Library/Saved Application State/com.fluidapp.Fluid*" 2>/dev/null || true
fi

