#!/bin/bash
# Uninstall script for Fontlab
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Fontlab..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Fontlab if running..."
pkill -f "Fontlab" 2>/dev/null || true

# Remove /Applications/FontLab 8.app
echo "Removing /Applications/FontLab 8.app..."
if [ -d "/Applications/FontLab 8.app" ]; then
    rm -rf "/Applications/FontLab 8.app" 2>/dev/null || true
elif [ -f "/Applications/FontLab 8.app" ]; then
    rm -f "/Applications/FontLab 8.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/FontLab
echo "Removing $HOME/Library/Application Support/FontLab..."
if [ -d "$HOME/Library/Application Support/FontLab" ]; then
    rm -rf "$HOME/Library/Application Support/FontLab" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/FontLab" ]; then
    rm -f "$HOME/Library/Application Support/FontLab" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.fontlab.fontlab8.plist
echo "Removing $HOME/Library/Preferences/com.fontlab.fontlab8.plist..."
if [ -d "$HOME/Library/Preferences/com.fontlab.fontlab8.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.fontlab.fontlab8.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.fontlab.fontlab8.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.fontlab.fontlab8.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.fontlab.fontlab8.savedState
echo "Removing $HOME/Library/Saved Application State/com.fontlab.fontlab8.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.fontlab.fontlab8.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.fontlab.fontlab8.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.fontlab.fontlab8.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.fontlab.fontlab8.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
