#!/bin/bash
# Uninstall script for FreeFileSync
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling FreeFileSync..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping FreeFileSync if running..."
pkill -f "FreeFileSync" 2>/dev/null || true

# Remove $HOME/Library/Application Support/FreeFileSync
echo "Removing $HOME/Library/Application Support/FreeFileSync..."
if [ -d "$HOME/Library/Application Support/FreeFileSync" ]; then
    rm -rf "$HOME/Library/Application Support/FreeFileSync" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/FreeFileSync" ]; then
    rm -f "$HOME/Library/Application Support/FreeFileSync" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.freefilesync.FreeFileSync.plist
echo "Removing $HOME/Library/Preferences/org.freefilesync.FreeFileSync.plist..."
if [ -d "$HOME/Library/Preferences/org.freefilesync.FreeFileSync.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.freefilesync.FreeFileSync.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.freefilesync.FreeFileSync.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.freefilesync.FreeFileSync.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
