#!/bin/bash
# Uninstall script for Front
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Front..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Front if running..."
pkill -f "Front" 2>/dev/null || true

# Remove /Applications/Front.app
echo "Removing /Applications/Front.app..."
if [ -d "/Applications/Front.app" ]; then
    rm -rf "/Applications/Front.app" 2>/dev/null || true
elif [ -f "/Applications/Front.app" ]; then
    rm -f "/Applications/Front.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Front
echo "Removing $HOME/Library/Application Support/Front..."
if [ -d "$HOME/Library/Application Support/Front" ]; then
    rm -rf "$HOME/Library/Application Support/Front" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Front" ]; then
    rm -f "$HOME/Library/Application Support/Front" 2>/dev/null || true
fi

# Remove $HOME/Library/FrontBoard
echo "Removing $HOME/Library/FrontBoard..."
if [ -d "$HOME/Library/FrontBoard" ]; then
    rm -rf "$HOME/Library/FrontBoard" 2>/dev/null || true
elif [ -f "$HOME/Library/FrontBoard" ]; then
    rm -f "$HOME/Library/FrontBoard" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Front
echo "Removing $HOME/Library/Logs/Front..."
if [ -d "$HOME/Library/Logs/Front" ]; then
    rm -rf "$HOME/Library/Logs/Front" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Front" ]; then
    rm -f "$HOME/Library/Logs/Front" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.frontapp.Front.plist
echo "Removing $HOME/Library/Preferences/com.frontapp.Front.plist..."
if [ -d "$HOME/Library/Preferences/com.frontapp.Front.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.frontapp.Front.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.frontapp.Front.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.frontapp.Front.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.frontapp.Front.savedState
echo "Removing $HOME/Library/Saved Application State/com.frontapp.Front.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.frontapp.Front.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.frontapp.Front.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.frontapp.Front.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.frontapp.Front.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
